<?phpinclude_once 'UserInviteInfo.class.php';class User{    public $UserID = -1;    public $SecurityLevel = -1;    public $TimezoneID = -1;    public $UserName = "";    public $FirstName = "";    public $LastName = "";    public $EmailAddress = "";    public $IsPremiumMember = 0;    public $Gender = "";    public $Birthdate = "";    public $Autobiography = "";    public $SendReminderEmails = false;    public $ReminderEmailAddress = "";    public $ReminderEmailSendInterval = -1;    public $GamePlatforms = [];    public $UserInviteInfo = null;    public function __construct($uid, $sl, $tID, $un, $fn, $ln, $ea, $ipm, $gn,                                 $bd, $ab, $gplatforms, $sre = false, $rea = "", $si = -1)    {        $this->UserID = $uid;        $this->SecurityLevel = $sl;        $this->TimezoneID = $tID;        $this->UserName = $un;        $this->FirstName = $fn;        $this->LastName = $ln;        $this->EmailAddress = $ea;        $this->IsPremiumMember = $ipm;        $this->Gender = $gn;        $this->Birthdate = $bd;        $this->Autobiography = $ab;        $this->SendReminderEmails = $sre;        $this->ReminderEmailAddress = $rea;        $this->ReminderEmailSendInterval = $si;                $this->GamePlatforms = $gplatforms;        $this->UserInviteInfo = UserInviteInfo::constructDefaultUserInviteInfo();    }    public static function constructDefaultUser()    {        $instance = new self(-1, -1, -1, "", "", "", "", 0, "", "", "", array());        return $instance;    }}