<?phpclass UserInviteInfo{        public $UserIdInviter = -1;    public $UserIdInvitee = -1;    public $InviteFromLoggedInUser = false;    public $InviteToLoggedInUser = false;    public $ReceivedInviteReply = '';    public $SentInviteReply = '';    public function __construct($usrc, $udst, $ifm, $itm, $rip, $sip)    {	$this->UserIdInviter = $usrc;	$this->UserIdInvitee = $udst;	$this->InviteFromLoggedInUser = $ifm;	$this->InviteToLoggedInUser = $itm;	$this->ReceivedInviteReply = $rip;	$this->SentInviteReply = $sip;    }    public static function constructDefaultUserInviteInfo()    {	$instance = new self(-1, -1, false, false, '', '');	return $instance;    }}